INSERT INTO `category` (`name`, `primary_image_url` , `first_order_category`, `second_order_category`, `third_order_category`,`title`, `subtitle`, `content_text`, `description`) VALUES ('Čajevke', 'images/12-2020/piramidalni.jpg',false,false,true,'<p>Ruže Čajevke</p>', '<p>Krupni cvetovi idealni za svaki vrt ali i vazu</p>', '<p>Čajnohibridne (Tea hibridi (T.H.)) ruže karakteriše snažan porast i visoki izdanci sa krupnim cvetom na kraju svake grane. Biljke su visine do 1 m, a najčešće između 60 i 80 cm ( u zavisnosti od sorte mesta sađenja i klime). Sadnice su plemenite forme, najčešće mirisne i pogodne za rezanje, krupnih cvetova i velikog izbora boja. Mogu se saditi u svim tipovima vrtova. Sade se u grupama, vrstama, gredicama, pojedinačno ili vezano sa drugim biljkama. Rastojanje pri sadnji je 40- 45 cm ili 5-6 komada po kvadratnom metru.</p>', '<p>Ruže Čajevke</p>
INSERT INTO `category` (`name`, `primary_image_url` , `first_order_category`, `second_order_category`, `third_order_category`,`title`, `subtitle`, `content_text`, `description`) VALUES ('Mnogocvetnice', 'images/12-2020/kuglaste.jpg',false,false,true, '<p>Mnogocvetnice | Polijante</p>', '<p>Cvetni masivni pogodni za svaki prostor</p>', '<p>Biljke uspravnog i žbunastog rasta, visokih izdanaka od 40-90 cm (u zavisnosti od sorte, mesta sađenja i klime); često sa mirisnim cvetovima, koji se formiraju u buketima. Cvetaju obilno cele sezone, pa se sađenjem u masi postižu snažni efekti. Pogodne su za sađenje: kod svih tipova kućnih vrtova, pored staza, u parkovima, pored ulica, kao cvetne žive ograde, leje i bordure. Treba težiti jednobojnim efektima i voditi računa o skladu boja i visina, za višebojne grupe. Sade se na rastojanju od 30- 40 cm ili 6-8 biljaka po metru kvadratnom.</p>', '<p>Mnogocvetnice | Polijante</p>
INSERT INTO `category` (`name`, `primary_image_url` , `first_order_category`, `second_order_category`, `third_order_category`,`title`, `subtitle`, `content_text`, `description`) VALUES ('Penjačice', 'images/12-2020/zbunasti.jpg',false,false,true, '<p>Penjačice | Puzavice</p>', '<p>Prirodni cvetni zidovi za novu dimenziju svakog vrta</p>', '<p>Divnih cvetova, snažnog rasta, visine do 4 m. Cvetaju cele sezone. Pogodne su za formiranje pergola, hladnjaka, cvetnih živih ograda, maskiranje zidova i neuglednih pozadina. Biljke imaju visoke izdanke i ako želimo da ostanu uspravne, moramo tražiti oslonac; u suprotnom će padati na dole, što može izgledati privlačno ako su u vrsti. Ako im klima i mesto sađenja odgovaraju, zavosno od sorte, mogu narasti i do 4 metra. Obavezno ih skraćivati svake godine za trećinu. Sade se na rastojanju 1-1,5 metra.</p>', '<p>Penjačice | Puzavice</p>
INSERT INTO `category` (`name`, `primary_image_url` , `first_order_category`, `second_order_category`, `third_order_category`,`title`, `subtitle`, `content_text`, `description`) VALUES ('Grmolike', 'images/12-2020/cajevke.jpg',false,false,true, '<p>Grmolike</p>', '<p>Žbunovite , bujne , ukrasne buketuše za prirodni izgled vrta</p>', '<p>Grmolike imaju širok spektar korišćenja. Od njih se prave cvetne žive ograde, obojene cvetne leje, čak i male šume. Po pravilu se sade pored ograde. Za pojedinačno sađenje se koristi štap kao oslonac. Opšta karakteristika im je mnogocvetnost, otpornost na bolesti i niske temperature, ali i izrazito jak rast. Biljke su uspravnog rasta ili žbunaste obično izdanaka visokih do 2 m (u zavisnosti od sorte, mesta i klime), mnogobrojnih raskošnih cvetova i divnih mirisa. Sade se na rastojanju 60-100 cm ili 1-2 sadnice po metru kvadratnom, u zavisnosti od bujnosti i sečenja izdanaka.</p>', '<p>Grmolike</p>
INSERT INTO `category` (`name`, `primary_image_url` , `first_order_category`, `second_order_category`, `third_order_category`,`title`, `subtitle`, `content_text`, `description`) VALUES ('Minijaturne', 'images/12-2020/mnogosvetnice.jpg',false,false,true, '<p>Minijaturne</p>', '<p>Male i otmene ruže prikladne za moderne položaje</p>', '<p>Ove male princeze među ružama pogodne su za sadnju na balkonima, u saksijama i žardinjerama, kamenjarima, u kombinaciji sa niskim četinarima, ali divno izgledaju i pored staza i kao formirane bordure. Osnovna odlika minijaturnih ruža je njihova veličina. Pored niskog rasta koji dostiže visinu do 35 cm, imaju i male listove, ali i manje cvetove od standardnih ruža. Tokom cele vegetacije bogato i neprestano se pojavljuju sitni cvetovi. Sade se na rastojanju od 25-30 cm ili 10-12 biljaka po metru kvadratnom. Ako se sade u saksije prečnik i dubina posude mora biti najmanje 30 cm.</p>', '<p>Minijaturne</p>
INSERT INTO `category` (`name`, `primary_image_url` , `first_order_category`, `second_order_category`, `third_order_category`,`title`, `subtitle`, `content_text`, `description`) VALUES ('Polegle', 'images/12-2020/mnogosvetnice.jpg',false,false,true, '<p>Polegle Ruže</p>', '<p>Cvetne alternativne za monotone zelene površine</p>', '<p>Spadaju u novu grupu ruža koje su zadnjih godina veoma popularne i koje se stalno uvećavaju novim selekcijama. Imaju bujnu vegetaciju, rastu poleglo pokrivajući zemlju, praveći pravu malu šumu. Sade se po parkovima i vrtovima, kao dopuna drugim biljkama, ali i kao samostalne celine. Odlične su na padinama i kao ograde. Ne moraju se orezivati redovno, već se to može uraditi jednom u 3-4 godine. Veoma su otporne na bolesti. Zbog velike površine koju pokrivaju veoma su ekonomične. Dele se u dve grupe: I grupa: visine do 30 cm, priljubljuju se uz zemlju, ako se posade na balkon, mnoštvo cvetova će dekorativno padati na dole. Sade se na rastojanju 60- 80 cm ili 2-3 biljake po metru kvadratnom II grupa: imaju bujniji porast, u visinu od 40 – 100 cm i isto toliko u širinu. Sade se na rastojanju 40- 60 cm ili 3-6 biljaka po metru kvadratnom</p>', '<p>Polegle Ruže</p>
INSERT INTO `category` (`name`, `primary_image_url` , `first_order_category`, `second_order_category`, `third_order_category`,`title`, `subtitle`, `content_text`, `description`) VALUES ('Engleske', 'images/12-2020/mnogosvetnice.jpg',false,false,true, '<p>Engleske Ruže</p>', '<p>Najbolje osobine starinskih ruža za moderne vrtove</p>', '<p>Engleske ruže predstavljaju grupu ruža koja je uspela da iskoristi genetski potencijal starinskih ruža njihovim ukrštanjem sa savremenim selekcijama i na taj način proizvede novu gupu ruža u svetu poznatu kao New English Roses. Naziv su dobile po selekcionaru koji je uspeo da ih stvori, a potiče iz Engleske. U modernom baštovanstu glavni problem starinskih sorti je bilo njihovo cvetanje koje se dešava samo jednom u toku sezone  u proleće i nakon toga biljka više ne proizvodi cvet do sledeće godine. Stoga je ideja prilikom stvaranja ove grupe ruža bila da  nastanu sorte koje će cvetati tokom cele godine, imati izgled starinskih cvetova koji su veoma krupni i mirisni sa izrazito velikim brojem latica. Danas je poznat veliki broj sorti koje pripadaju Engleskim ružama, koji se iz godine u godinu uvećava.</p>', '<p>Engleske Ruže</p>
